mapper.select:
select
from tab_account u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	tab_account u 
WHERE
	u.status <> 9 
	 AND u.status = #{page.cnds.status}
	 AND u.loginname = #{page.cnds.loginname}
	 AND u.name like #{page.cnds.name}
	 AND u.name = #{page.cnds.nameEq}
	 AND u.mobile = #{page.cnds.mobile}
	 AND u.email = #{page.cnds.email}
	 AND u.birthday = #{page.cnds.birthday}
	 AND u.birthday >= #{page.cnds.birthdayGe}
	 AND u.birthday <= #{page.cnds.birthdayLe}
	 AND u.sex = #{page.cnds.sex}
	 AND u.lastPasswdDate >= #{page.cnds.lastPasswdDateGe}
	 AND u.lastPasswdDate <= #{page.cnds.lastPasswdDateLe}
	 AND u.lastLoginDate >= #{page.cnds.lastLoginDateGe}
	 AND u.lastLoginDate <= #{page.cnds.lastLoginDateLe}
	 AND u.wechatOpenId = #{page.cnds.wechatOpenId}
	 AND u.alipayOpenId = #{page.cnds.alipayOpenId}

	
		order by  u.${ord} 
	
	
		order by u.id

