mapper.update:
update 
	tab_account 
set 
	updateTime=now()
	,name=#{entity.name}
	,mobile=#{entity.mobile}
	,email=#{entity.email}
	,birthday=#{entity.birthday}
	,sex=#{entity.sex}
	,ico=#{entity.ico}
	,remark=#{entity.remark}
	 ,passwd=#{entity.passwd},salt=#{entity.salt}
where 
	id=#{entity.id} and uuid=#{entity.uuid}

update 
	tab_account 
set 
	updateTime=now(),wechatOpenId=#{openid}
where 
	id=#{id}

update 
	tab_account 
set 
	updateTime=now(),alipayOpenId=#{openid}
where 
	id=#{id}
