mapper.select:
select
from tab_black_his u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	tab_black_his u 
WHERE
	u.status <> 9 
	 AND u.status = #{page.cnds.status}
	 AND u.hisId = #{page.cnds.hisId}
	 AND u.blackTarget = #{page.cnds.blackTarget}
	 AND u.type = #{page.cnds.type}
	 AND u.blackTime = #{page.cnds.blackTime}
	 AND u.blackTime >= #{page.cnds.blackTimeGe}
	 AND u.blackTime <= #{page.cnds.blackTimeLe}
	 AND u.reason = #{page.cnds.reason}
	 AND u.operator = #{page.cnds.operator}

	
		order by  u.${ord} 
	
	
		order by u.id

