	 AND u.status = #{query.cnds.status}
	 AND u.hisId = #{query.cnds.hisId}
	 AND u.blackTarget = #{query.cnds.blackTarget}
	 AND u.type = #{query.cnds.type}
	 AND u.blackTime = #{query.cnds.blackTime}
	 AND u.blackTime >= #{query.cnds.blackTimeGe}
	 AND u.blackTime <= #{query.cnds.blackTimeLe}
	 AND u.reason = #{query.cnds.reason}
