mapper.update:
update 
	tab_black_his 
set 
	updateTime=now()
	,hisId=#{entity.hisId}
	,blackTarget=#{entity.blackTarget}
	,type=#{entity.type}
	,blackTime=#{entity.blackTime}
	,reason=#{entity.reason}
	,operator=#{entity.operator}
	,remark=#{entity.remark}
	 ,status=#{entity.status}
where 
	id=#{entity.id} and uuid=#{entity.uuid}
