mapper.select:
select
from tab_lock u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	tab_lock u 
WHERE
	u.status <> 9 
	 AND u.status = #{page.cnds.status}
	 AND u.lockedTarget = #{page.cnds.lockedTarget}
	 AND u.type = #{page.cnds.type}
	 AND u.count = #{page.cnds.count}
	 AND u.reason = #{page.cnds.reason}
	 AND u.lockedTime = #{page.cnds.lockedTime}
	 AND u.lockedTime >= #{page.cnds.lockedTimeGe}
	 AND u.lockedTime <= #{page.cnds.lockedTimeLe}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	tab_lock u 
WHERE
	u.status <> 9 
	 AND u.status = #{page.cnds.status}
	 AND u.lockedTarget = #{page.cnds.lockedTarget}
	 AND u.type = #{page.cnds.type}
	 AND u.count = #{page.cnds.count}
	 AND u.reason = #{page.cnds.reason}
	 AND u.lockedTime = #{page.cnds.lockedTime}
	 AND u.lockedTime >= #{page.cnds.lockedTimeGe}
	 AND u.lockedTime <= #{page.cnds.lockedTimeLe}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	tab_lock u
WHERE
	u.status <> 9 
	 AND u.status = #{query.cnds.status}
	 AND u.lockedTarget = #{query.cnds.lockedTarget}
	 AND u.type = #{query.cnds.type}
	 AND u.count = #{query.cnds.count}
	 AND u.reason = #{query.cnds.reason}
	 AND u.lockedTime = #{query.cnds.lockedTime}
	 AND u.lockedTime >= #{query.cnds.lockedTimeGe}
	 AND u.lockedTime <= #{query.cnds.lockedTimeLe}

	
		order by  u.${ord} 
	
	
		order by u.status

mapper.update:
update 
	tab_lock 
set 
	updateTime=now()
	,lockedTarget=#{entity.lockedTarget}
	,type=#{entity.type}
	,count=#{entity.count}
	,err=#{entity.err}
	,reason=#{entity.reason}
	,lockedTime=#{entity.lockedTime}
	,remark=#{entity.remark}
	 ,status=#{entity.status}
where 
	id=#{entity.id} and uuid=#{entity.uuid}
