mapper.select:
select
from tab_login_his u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	tab_login_his u 
WHERE
	u.status <> 9 
	 AND u.status = #{page.cnds.status}
	 AND u.loginname = #{page.cnds.loginname}
	 AND u.loginTime = #{page.cnds.loginTime}
	 AND u.loginTime >= #{page.cnds.loginTimeGe}
	 AND u.loginTime <= #{page.cnds.loginTimeLe}
	 AND u.appCode = #{page.cnds.appCode}
	 AND u.service = #{page.cnds.service}
	 AND u.ip = #{page.cnds.ip}
	 AND u.mobi = #{page.cnds.mobi}
	 AND u.mobileBrand = #{page.cnds.mobileBrand}
	 AND u.os = #{page.cnds.os}
	 AND u.osVersion = #{page.cnds.osVersion}
	 AND u.browser = #{page.cnds.browser}
	 AND u.browserVersion = #{page.cnds.browserVersion}

	
		order by  u.${ord} 
	
	
		order by u.id

